<template>
  <div :class="['zvm-doc-simulator', { 'zvm-doc-simulator-fixed': isFixed }]">
    <div class="zvm-doc-simulator__nav">
      <div class="zvm-doc-simulator__url">{{ iframeHostName }}</div>
      <div class="zvm-doc-simulator__reload" @click="reloadIframe" />
    </div>
    <iframe ref="iframe" :src="srcWithTimestamp" :style="simulatorStyle" frameborder="0" />
  </div>
</template>

<script>
export default {
  name: 'zvm-doc-simulator',

  props: {
    src: String
  },

  data() {
    return {
      scrollTop: window.scrollY,
      iframeHostName: '',
      windowHeight: window.innerHeight
    };
  },

  mounted() {
    window.addEventListener('scroll', () => {
      this.scrollTop = window.scrollY;
    });
    window.addEventListener('resize', () => {
      this.windowHeight = window.innerHeight;
    });

    const { iframe } = this.$refs;
    if (iframe) {
      if (iframe.contentDocument.readyState === 'complete') {
        setTimeout(this.onSrcChanged, 0);
      } else {
        iframe.onload = () => {
          this.onSrcChanged();
        };
      }
    }
  },

  watch: {
    src() {
      this.onSrcChanged();
    }
  },

  computed: {
    srcWithTimestamp() {
      const now = `rand=${Date.now()}`;
      return this.src + (this.src.indexOf('?') === -1 ? '?' : '&') + now;
    },

    isFixed() {
      return this.scrollTop > 60;
    },

    simulatorStyle() {
      const height = Math.min(580, this.windowHeight - 150);
      return {
        height: height + 'px'
      };
    }
  },

  methods: {
    reloadIframe() {
      const { iframe } = this.$refs;
      if (iframe && iframe.contentWindow) {
        iframe.contentWindow.location.reload();
      }
    },
    onSrcChanged() {
      const { iframe } = this.$refs;
      if (iframe && iframe.contentWindow) {
        if (this.src.indexOf('://') !== -1) {
          this.iframeHostName = this.src.split('://')[1].split('/')[0];
        } else {
          this.iframeHostName = iframe.contentWindow.location.host || location.host;
        }
      }
    }
  }
};
</script>

<style lang="postcss">
@import '../style/variable';

.zvm-doc-simulator {
  z-index: 1;
  overflow: hidden;
  position: absolute;
  border-radius: 6px;
  background: #fafafa;
  box-sizing: border-box;
  right: $zvm-doc-padding;
  width: $zvm-doc-simulator-width;
  min-width: $zvm-doc-simulator-width;
  top: calc($zvm-doc-padding + $zvm-doc-header-top-height);
  box-shadow: rgba(0, 0, 0, 0.2) 0 1px 4px, rgba(0, 0, 0, 0.2) 0 1px 2px;

  @media (max-width: 1300px) {
    width: $zvm-doc-simulator-small-width;
    min-width: $zvm-doc-simulator-small-width;
  }

  @media (max-width: 1100px) {
    left: 750px;
    right: auto;
  }

  @media (min-width: $zvm-doc-row-max-width) {
    right: 50%;
    margin-right: calc(-$zvm-doc-row-max-width/2 + 40px);
  }

  &-fixed {
    position: fixed;
    top: $zvm-doc-padding;
  }

  iframe {
    width: 100%;
    display: block;
  }

  &__nav {
    height: 60px;
    background: url('data:image/png;base64,') no-repeat;
    background-size: 100%;
  }

  &__url {
    left: 40px;
    top: 23px;
    right: 40px;
    font-size: 14px;
    position: absolute;
    text-align: center;
    font-weight: bold;
    line-height: 28px;
    font-family: PingFang SC, Helvetica Neue, Helvetica, Arial, sans-serif;

    @media (max-width: 1300px) {
      top: 21px;
      line-height: 24px;
    }
  }

  &__reload {
    top: 25px;
    right: 10px;
    width: 28px;
    height: 28px;
    cursor: pointer;
    position: absolute;
  }
}
</style>
